mapper.select:
select * from task_record

	id=#{id}
	and id in #{i}
	and jira_no=#{jiraNo}
	and jira_no in #{jira}
	and demand_name like concat('%',#{demandName},'%')
	and status=#{status}
	and iterate_week=#{iterateWeek}
	and create_time >= #{startTime}
	and create_time <= #{endTime}
	and env = #{env}
	and (create_by = #{createBy} or principal = #{createBy})

mapper.update:
update task_record
demand_name=#{demandName}, demand_type=#{demandType}, relate_demand=#{relateDemand}, principal=#{principal}, remark=#{remark}, iterate_week=#{iterateWeek}, out_dlls=#{outDlls}, status=#{status}, update_by=#{updateBy}
where jira_no=#{jiraNo} and env=#{env} limit 1

update task_record
demand_name=#{demandName}, demand_type=#{demandType}, relate_demand=#{relateDemand}, principal=#{principal}, remark=#{remark}, iterate_week=#{iterateWeek}, out_dlls=#{outDlls}, status=#{status}, update_by=#{updateBy}
where id=#{id} limit 1
